/*Table structure for table `reply_comment` */

DROP TABLE IF EXISTS `reply_comment`;

CREATE TABLE `reply_comment` (
  `Id` int(11) NOT NULL AUTO_INCREMENT,
  `comment_id` int(11) NOT NULL DEFAULT '0' COMMENT '评论id',
  `reply_user_id` varchar(50) NOT NULL DEFAULT '' COMMENT '回复评论者ID',
  `comment_user_id` varchar(50) NOT NULL DEFAULT '' COMMENT '评论者id',
  `reply_content` varchar(100) NOT NULL DEFAULT '',
  `reply_time` date NOT NULL DEFAULT '0000-00-00',
  `delete_state` int(2) NOT NULL DEFAULT '0',
  PRIMARY KEY (`Id`)
) ENGINE=InnoDB AUTO_INCREMENT=24 DEFAULT CHARSET=utf8;

