/*Table structure for table `t_user` */

DROP TABLE IF EXISTS `t_user`;

CREATE TABLE `t_user` (
  `user_id` varchar(18) NOT NULL COMMENT '用户账号',
  `password` varchar(50) NOT NULL,
  `nickname` varchar(20) DEFAULT NULL COMMENT '昵称',
  `gender` varchar(20) DEFAULT NULL COMMENT '性别',
  `birth` date DEFAULT NULL COMMENT '出身年月',
  `age` int(11) DEFAULT NULL COMMENT '年龄',
  `email` varchar(18) NOT NULL COMMENT '邮件地址',
  `qq` varchar(18) DEFAULT NULL COMMENT 'qq号',
  `register_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '注册时间，使用时间戳',
  `address` varchar(100) DEFAULT NULL COMMENT '现住址',
  `school` varchar(100) DEFAULT NULL COMMENT '学校',
  `profession` varchar(100) DEFAULT NULL COMMENT '职业',
  `imgurl` varchar(100) NOT NULL COMMENT '头像地址',
  `signal` varchar(100) DEFAULT NULL COMMENT '个性签名',
  `description` varchar(255) DEFAULT NULL COMMENT '个人说明描述',
  `active_code` varchar(100) NOT NULL COMMENT '激活码',
  `active` varchar(11) NOT NULL DEFAULT '0' COMMENT '激活状态',
  `state` int(8) NOT NULL DEFAULT '0' COMMENT '是否锁定',
  PRIMARY KEY (`user_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='同学录的普通用户表';

